function startCountdown(n){var t=n.countdownValue||10;n.countdownInterval=setInterval(function(){if(n.innerText=t,t--,t<0){clearInterval(n.countdownInterval),n.style.display="none";var o=n.nextElementSibling;o.style.display="inline-flex",buttons.forEach(function(n){n.disabled=!1})}},1250)}function Countdown(n){buttons.forEach(function(n){n.disabled=!0}),n.disabled=!0,n.classList.add("countdown"),startCountdown(n)}function pauseCountdowns(){buttons.forEach(function(n){clearInterval(n.countdownInterval),n.countdownValue=parseInt(n.innerText)})}function resumeCountdowns(){buttons.forEach(function(n){n.classList.contains("countdown")&&startCountdown(n)})}var buttons=document.querySelectorAll(".dupBtn");document.addEventListener("visibilitychange",function(){"hidden"===document.visibilityState?pauseCountdowns():"visible"===document.visibilityState&&resumeCountdowns()});