var buttons=document.querySelectorAll(".dupBtn");function startCountdown(b){var a=b.countdownValue||10;b.countdownInterval=setInterval(function(){b.innerText=a;a--;if(a<0){clearInterval(b.countdownInterval);b.style.display="none";var c=b.nextElementSibling;c.style.display="inline-flex";buttons.forEach(function(d){d.disabled=false})}},1250)}function Countdown(a){buttons.forEach(function(b){b.disabled=true});a.disabled=true;a.classList.add("countdown");startCountdown(a)}document.addEventListener("visibilitychange",function(){if(document.visibilityState==="hidden"){pauseCountdowns()}else{if(document.visibilityState==="visible"){resumeCountdowns()}}});function pauseCountdowns(){buttons.forEach(function(a){clearInterval(a.countdownInterval);a.countdownValue=parseInt(a.innerText)})}function resumeCountdowns(){buttons.forEach(function(a){if(a.classList.contains("countdown")){startCountdown(a)}})};